mapper.update:
update user
set password=#{password}
where phone = #{phone}

update user
set photo=#{photo}
where id = #{id}

update user
set nickname=#{nickname},
    sex=#{sex}
where id = #{id}

mapper.select:
select id
from user
where phone = #{phone}

select id
from user
where phone = #{phone}
  and password = #{password}
